@Component({
  selector: 'app-animal',
  templateUrl: './animal.component.html',
  styleUrls: ['./animal.component.css']
})
export class AnimalComponent {
audio(){

}
}
<div class="animal">
    <div>
    <img src="/assets/images/dog.jpg" width="150px" height="150px" ><a href="javascript:playSound('dog')">Dog</a><br />
    <audio id="dog" controls>
      <source src="/assets/sound/dog_barking-6296.mp3" type="audio/mpeg">
    </audio>
  </div>
  <div>
    <img src="/assets/images/horse.jpg" width="150px" height="150px"><a href="javascript:playSound('horse')">Dog</a><br />
    <audio id="horse"controls>
      <source src="/assets/sound/single-horse-galopp-6152.mp3" type="audio/mpeg">
    </audio>
  </div>
  </div>
  <div> 
    <script>
      function playSound(animal) {
  document.getElementById(animal).play();
};

    </script>
  </div>
